function changeCursor(e){e.results.length>0&&"buildings_app"==e.results[0].graphic.layer.title?document.getElementById("viewDiv").style.cursor="pointer":document.getElementById("viewDiv").style.cursor="default"}view.on("pointer-move",function(e){var t={x:e.x,y:e.y};view.hitTest(t).then(function(e){e.results.length>0&&changeCursor(e)})});